  //increase counter
  const increase = () => {
    setCounter(count => count + 1);
  };

  //decrease counter
  const decrease = () => {
    setCounter(count => count - 1);
  };

  //reset counter 
  const reset = () =>{
    setCounter(0)
  }

  return (
    <form>
  <label>
    Name:
    <input type="text" name="name" />
